    var regionArray = new Array("United States", "Africa", "Asia", "Caribbean", "Europe", "Middle East", "The Americas", "Oceania");
    

    var dataPromises = [];

    var regionGroups;

    var regionGroupItems = new WinJS.Binding.List();

    function getRegionGroups() {

        var regionGroupslist = [];
        var len = regionArray.length;
        for (var i = 0; i < len; i++) {
            var obj = {
                key: WinJS.Resources.getString(regionArray[i]).value,
                title: WinJS.Resources.getString(regionArray[i]).value,
                subtitle: regionArray[i],
                backgroundImage: darkGray,
                description: "",
                url: 'xml/Country.xml',
                acquireSyndication: acquireSyndication, dataPromise: null
            };
            regionGroupslist.push(obj);
        }
        regionGroups = regionGroupslist

        // Get the content for each feed in the blogs array
        regionGroups.forEach(function (region) {
            region.dataPromise = region.acquireSyndication(region.url);
            dataPromises.push(region.dataPromise);
        });

        // Return when all asynchronous operations are complete
        return WinJS.Promise.join(dataPromises).then(function () {
            return regionGroups;
        });
    }

    function acquireSyndication(url) {
        // Call xhr for the URL to get results asynchronously
        return WinJS.xhr({ url: url });
    }

    function getRegionCountries()
    {
        getRegionGroups().then(function () {
            // Process each region group
            regionGroups.forEach(function (region) {
                region.dataPromise.then(function (regionResponse) {
                    var regionSyndication = regionResponse.responseXML;

                    // Get the info for each region 
                    var regions = regionSyndication.querySelectorAll("region");

                    // Process each region
                    for (var regionIndex = 0; regionIndex < regions.length; regionIndex++) {

                        if (region.title == WinJS.Resources.getString(regions[regionIndex].attributes.getNamedItem("name").textContent).value)
                        {
                            // Get the info for each region countries
                            var countries = regions[regionIndex].querySelectorAll("c");

                            // process each country
                            for (var countryIndex = 0; countryIndex < countries.length; countryIndex++) {

                                regionGroupItems.push({
                                    group: region,
                                    title: WinJS.Resources.getString(countries[countryIndex].attributes.getNamedItem("name").textContent).value,
                                    subtitle: countries[countryIndex].querySelector("abbr").textContent,
                                    content: "",
                                    backgroundImage: "../Flag/" + countries[countryIndex].querySelector("flag").textContent
                                });
                            }
                        }
                    }
                });
            });
        });

        return regionGroupItems;
    }

    // Get a reference for an item, using the group key and item title as a
    // unique reference to the item that can be easily serialized.
    function getItemReference(item) {
        return [item.group.key, item.title];
    }

    function resolveGroupReference(key) {
        for (var i = 0; i < groupedItems.groups.length; i++) {
            if (groupedItems.groups.getAt(i).key === key) {
                return groupedItems.groups.getAt(i);
            }
        }
    }

    function resolveItemReference(reference) {
        for (var i = 0; i < groupedItems.length; i++) {
            var item = groupedItems.getAt(i);
            if (item.group.key === reference[0] && item.title === reference[1]) {
                return item;
            }
        }
    }

    // This function returns a WinJS.Binding.List containing only the items
    // that belong to the provided group.
    function getItemsFromGroup(group) {
        return list.createFiltered(function (item) { return item.group.key === group.key; });
    }

    //var list = new WinJS.Binding.List();
    var list = getRegionCountries();
    var groupedItems = list.createGrouped(
        function groupKeySelector(item) { return item.group.key; },
        function groupDataSelector(item) { return item.group; }
    );

